import "./singlePost.css";
import Img4 from "../assets/212.jpg";
import { useLocation } from "react-router-dom";
import { useEffect } from "react";
import axios from "axios";

export default function SinglePost() {
  const location = useLocation();
  const path = location.pathname.split("/")[2];

  useEffect(() => {
    const getPost = async () => {
      const res = axios.get("/posts/" + path);
      console.log(res);
    };
    getPost();
  }, [path]);

  return (
    <div className="singlePost">
      <div className="singlePostWrapper">
        <img src={Img4} alt="" className="singlePostImg" />
        <h1 className="singlePostTitle">
          This is my First Blog.
          <div className="singlePostEdit">
            <i className="singlePostIcon fa-solid fa-pen-to-square"></i>
            <i className="singlePostIcon fa-solid fa-trash-can"></i>
          </div>
        </h1>
        <div className="singlePostInfo">
          <span className="singlePostAuthor">
            Author: <b>Satyam</b>
          </span>
          <span className="singlePostDate">1 hour ago</span>
        </div>
        <p className="singlePostDesc">
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Architecto,
          deserunt et, quisquam nostrum esse, expedita sequi commodi ipsum ipsa
          suscipit veniam magnam. Suscipit ex accusamus dignissimos distinctio
          fugit fuga repellat. Lorem ipsum dolor sit amet consectetur
          adipisicing elit. Architecto, deserunt et, quisquam nostrum esse,
          expedita sequi commodi ipsum ipsa suscipit veniam magnam. Suscipit ex
          accusamus dignissimos distinctio fugit fuga repellat. Lorem ipsum
          dolor sit amet consectetur adipisicing elit. Architecto, deserunt et,
          quisquam nostrum esse, expedita sequi commodi ipsum ipsa suscipit
          veniam magnam. Suscipit ex accusamus dignissimos distinctio fugit fuga
          repellat. Lorem ipsum dolor sit amet consectetur adipisicing elit.
          Architecto, deserunt et, quisquam nostrum esse, expedita sequi commodi
          ipsum ipsa suscipit veniam magnam. Suscipit ex accusamus dignissimos
          distinctio fugit fuga repellat.
        </p>
      </div>
    </div>
  );
}

